mapper.update:
update h_dict_type

    
        dictName = #{dictName}
    
    
        dictType = #{dictType}
    
    
        state = #{state}
    
    
        remark = #{remark}
    
    updateBy = #{updateBy},
    updateTime = #{updateTime}

where dictId = #{dictId}

mapper.insert:
insert into h_dict_data (orderNum, dictLabel, dictValue, dictType, defaultState, createBy, createTime, remark)
values (#{orderNum}, #{dictLabel}, #{dictValue}, #{dictType}, #{defaultState}, #{createBy}, getDate(),
        #{remark});
