mapper.update:
update t_role

    
        name = #{name},
    
    
        orderNum = #{orderNum},
    
    
        remark = #{remark},
    
    updateBy = #{updateBy}

where roleId = #{id}

mapper.select:
select t_role.*
from t_role,
     t_user_role
where t_user_role.userId = #{userId}
  and t_user_role.roleId = t_role.id
